function sidebarSize(){var a=$("body").height()-70,b=$(".main").height()+70;b>a?$(".sidebar").css({height:b}):$(".sidebar").css({height:a})}$(document).ready(function(){$(":checkbox").checkbox(),$("select").selectpicker({style:"btn btn-info",menuStyle:"dropdown-inverse"});new wysihtml5.Editor("editor-textarea",{toolbar:"editor-toolbar",stylesheets:["styles/editor.css"],parserRules:wysihtml5ParserRules})}),$(window).resize(sidebarSize),$(window).bind("load",function(){sidebarSize()});
var txtOut;
function confirma(action, table, id, value){
	var txtConfirm;
	var txtReturn;
	if(action=="delete"){ txtConfirm = "deletar"; txtReturn = "deletado"; }
	if(action=="activate"){ txtConfirm = "ativar"; txtReturn = "ativado"; }
	if(action=="deactivate"){ txtConfirm = "desativar"; txtReturn = "desativado"; } 
	executarFuncao = confirm("Tem certeza que deseja "+txtConfirm+" o item selecionado?");
	txtOut = "O item foi "+txtReturn+" com sucesso!"; 
	if(executarFuncao)
		sendAjax(action, table, id, value);
	return false;
}
function sendAjax(action, table, id, value) {
    $.ajax({
      url: 'includes/activate.php',
      method: 'POST',
      data: {
        action: action,
        table: table,
        id: id,
        value: value
      },
      statusCode: {
  	    404: function() {
  	      alert( "page not found" );
  	    }
  	  },
      success: function(valRet) {
    	  alert(valRet);
    	  if(valRet=="ok"){
	    	  $("#"+id).remove();
	    	  $("#alerta").remove();    	 
	    	  $("h3").after("<div id='alerta' class='row'><div class='alert alert-info alert-dismissable'>" +
	    	  				"<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;</button>" +
	    	  				txtOut +" "+ valRet+
	    	  				"</div>" +
							"</div>");
    	  }else{
    		  alert(">>>"+valRet+"<<<<");
    	  }
        //alert("success!"+vari);
      }
    });
    return false;
  }

